import React from 'react';
import SwipeableViews from 'react-swipeable-views';
import Animated from 'animated/lib/targets/react-dom';

const styles = {
  root: {
    background: '#2196f3',
    padding: '0 50px',
  },
  slide: {
    padding: '24px 16px',
    color: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column',
    display: 'flex',
  },
  img: {
    width: 280,
    height: 280,
    display: 'block',
    marginBottom: 16,
  },
};

const albums = [
  {
    name: 'newest Samsung phone',
    src: 'https://www.sammobile.com/wp-content/uploads/2022/02/Galaxy-S22-Ultra-1-1200x675.jpg',
  },
  {
    name: 'Sumsung Galaxy',
    src: 'https://media.cnn.com/api/v1/images/stellar/prod/210811092029-1-samsung-galaxy-unpacked-family-cnn-underscored.jpg?q=w_2608,h_1547,x_0,y_0,c_fill',
  },
  {
    name: 'Google Pixel',
    src: 'data:image/jpeg;base64,',
  },
  {
    name: 'Iphone 12 ',
    src: 'data:image/jpeg;base64,',
  },
  {
    name: 'Iphone 4',
    src: 'data:image/jpeg;base64,',
  },
  
  {
    name: 'Apple Series',
    src: 'https://store.storeimages.cdn-apple.com/4982/as-images.apple.com/is/MN1L3ref_VW_34FR+watch-45-alum-starlight-nc-7s_VW_34FR_WF_CO?wid=375&hei=356&trim=1%2C0&fmt=p-jpg&qlt=95&.v=1645128544617%2C1631661833000',
  },
];

class Client extends React.Component {
  state = {
    index: 0,
    position: new Animated.Value(0),
  };

  handleChangeIndex = index => {
    this.setState({ index });
  };

  handleSwitch = (index, type) => {
    if (type === 'end') {
      Animated.spring(this.state.position, { toValue: index }).start();
      return;
    }
    this.state.position.setValue(index);
  };

  render() {
    const { index, position } = this.state;

    return (
      <SwipeableViews
        index={index}
        style={styles.root}
        onChangeIndex={this.handleChangeIndex}
        onSwitching={this.handleSwitch}
      >
        {albums.map((album, currentIndex) => {
          const inputRange = albums.map((_, i) => i);
          const scale = position.interpolate({
            inputRange,
            outputRange: inputRange.map(i => {
              return currentIndex === i ? 1 : 0.7;
            }),
          });
          const opacity = position.interpolate({
            inputRange,
            outputRange: inputRange.map(i => {
              return currentIndex === i ? 1 : 0.3;
            }),
          });
          const translateX = position.interpolate({
            inputRange,
            outputRange: inputRange.map(i => {
              return (100 / 2) * (i - currentIndex);
            }),
          });

          return (
            <Animated.div
              key={String(currentIndex)}
              style={Object.assign(
                {
                  opacity,
                  transform: [{ scale }, { translateX }],
                },
                styles.slide,
              )}
            >
              <img style={styles.img} src={album.src} alt="cover" />
              {album.name} 
            </Animated.div>
          );
        })}
      </SwipeableViews>
    );
  }
}

export default Client;
